console.log("connected to JavaScript"),document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll(".pokedex-list")[0],t=new DOMParser;fetch("https://pokeapi.co/api/v2/pokemon?limit=807/").then(e=>e.json()).then(e=>{return[...e.results]}).then(n=>{n.forEach(function(n){const p=t.parseFromString(`<li id=${n.name}></li>`,"text/html");e.appendChild(p.body.firstChild),fetch(`https://pokeapi.co/api/v2/pokemon/${n.name}`).then(e=>e.json()).then(e=>{n.images={...e.sprites},n.types=[...e.types],n.id=e.id;const p=n.types[1]?`<p class=${n.types[0].type.name}>${n.types[0].type.name}</p><p class=${n.types[1].type.name}>${n.types[1].type.name}</p>`:`<p class=${n.types[0].type.name}>${n.types[0].type.name}</p>`,s=t.parseFromString(`<a href="./pokemon.html#${n.id}"><div><p><span>ID: ${n.id}</span></p><p class="pokemon-name">${n.name}</p>${p}</div>\n              <img class="lazy" src="${n.images.front_default}" /><p class="view"> View More <p></a>`,"text/html");document.getElementById(`${n.name}`).appendChild(s.body.firstChild)})})})});